mapper.select:
SELECT count(1) AS total FROM urlcontent_check_2017 a WHERE a.ID IN ( SELECT c.CHECK_ID FROM personwash_task_to_check_data_relation c WHERE c.TASK_ID = #{taskId} )
AND a.CL_WORK_STATUS = #{clWorkStatus}
AND a.CL_REVIEW_STATUS = #{clReviewStatus}
AND a.CL_REVIEW_STATUS IS NULL

SELECT a.ID, a.IR_SID, a.IR_URLTITLE, a.IR_LASTTIME, a.CL_COUNT, a.CL_WORK_STATUS, a.CL_REVIEW_STATUS FROM urlcontent_check_2017 a WHERE a.ID IN ( SELECT c.CHECK_ID FROM personwash_task_to_check_data_relation c WHERE c.TASK_ID = #{taskId} )
AND a.CL_WORK_STATUS = #{clWorkStatus}
AND a.CL_REVIEW_STATUS = #{clReviewStatus}
AND a.CL_REVIEW_STATUS IS NULL
ORDER BY IR_SID ASC LIMIT ${page.startNo}, ${page.limit}

mapper.update:
UPDATE personwash_tasklist
SET RANDOM_CHECK_RATE = #{rate}
WHERE
    ID = #{taskId}
